// AdminConfigurationPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Switch from '@mui/material/Switch';
import TextField from '@mui/material/TextField';

function AdminConfigurationPage() {
  const navigate = useNavigate();
  const [contactPageEnabled, setContactPageEnabled] = useState(true);
  const [inputPageCount, setInputPageCount] = useState(1);
  const [outputPageCount, setOutputPageCount] = useState(1);

  const handleBack = () => {
    navigate('/admin');
  };

  const handleSave = () => {
    // Admin ayarlarını kaydetme işlemleri burada gerçekleştirilebilir
    console.log('Settings saved:', {
      contactPageEnabled,
      inputPageCount,
      outputPageCount,
    });
  };

  const handleGoToAppPage = () => {
    navigate(`/app/${inputPageCount}`); // /app/{inputPageCount} olarak yönlendirme yapılır
  };

  return (
    <Paper elevation={3}>
      <Box p={3}>
        <Typography variant="h5" gutterBottom>
          Admin Configuration Page
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Contact Page Settings:
        </Typography>
        <Switch
          checked={contactPageEnabled}
          onChange={(e) => setContactPageEnabled(e.target.checked)}
        />
        <Typography variant="subtitle1" gutterBottom>
          Input Page Count:
        </Typography>
        <TextField
          type="number"
          value={inputPageCount}
          onChange={(e) => setInputPageCount(Number(e.target.value))}
        />
        <Typography variant="subtitle1" gutterBottom>
          Output Page Count:
        </Typography>
        <TextField
          type="number"
          value={outputPageCount}
          onChange={(e) => setOutputPageCount(Number(e.target.value))}
        />
        <Button variant="contained" color="primary" onClick={handleSave}>
          Save Settings
        </Button>
        <Button variant="contained" color="secondary" onClick={handleBack}>
          Back to Admin Panel
        </Button>
        <Button variant="contained" onClick={handleGoToAppPage}>
          Go to App Page
        </Button>
      </Box>
    </Paper>
  );
}

export default AdminConfigurationPage;
