import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import SignInSide from '../component/SignInSide';

function Login() {
  const navigate = useNavigate(); // useNavigate kullanımı
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);

    const enteredEmail = data.get('email');
    const enteredPassword = data.get('password');

    // Burada gerçek bir doğrulama yapılmalı
    if (enteredEmail === 'doğruEmail' && enteredPassword === 'doğruŞifre') {
      // Doğru bilgiler girildiyse /admin sayfasına yönlendir
      navigate('/admin'); // useNavigate kullanarak yönlendirme
    } else {
      // Yanlış bilgiler girildiyse burada gerekli işlemleri yapabilirsiniz
      console.log('Hatalı giriş');
    }
  };

  return (
   
      <SignInSide handleSubmit={handleSubmit} />
    
  );
}

export default Login;
