import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom'; // useNavigate ekleyin

// ... (diğer kodlar)

export default function SignInSide() {
  const navigate = useNavigate(); // useNavigate kullanımı

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);

    const enteredEmail = data.get('email');
    const enteredPassword = data.get('password');

    // Kullanıcı giriş bilgilerini doğrula
    if (checkCredentials(enteredEmail, enteredPassword)) {
      // Doğru bilgiler girildiyse /admin sayfasına yönlendir
      navigate('/admin');
    } else {
      // Yanlış bilgiler girildiyse burada gerekli işlemleri yapabilirsiniz
      console.log('Hatalı giriş');
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      {/* ... (diğer JSX kodları) */}
      <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
        {/* ... (input ve diğer alanlar) */}
        <Button
          type="submit"
          fullWidth
          variant="contained"
          sx={{ mt: 3, mb: 2 }}
        >
          Sign In
        </Button>
        {/* ... (diğer JSX kodları) */}
      </Box>
      {/* ... (diğer JSX kodları) */}
    </ThemeProvider>
  );
}
