import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function LeadGenerationPage() {
  const location = useLocation();
  const inputPages = location.state.inputPages || [];
  const [currentPage, setCurrentPage] = useState(0); // Start with the first page
  const currentPageData = inputPages[currentPage];

  useEffect(() => {
    // Update page content based on currentPageData
    // ...
  }, [currentPageData]);

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  return (
    <div>
      <h1>Lead Üretme Sayfası</h1>
      <div>
        {/* Render currentPageData */}
        <h2>{currentPageData.title}</h2>
        <p>{currentPageData.description}</p>
        {/* Render inputs and select based on currentPageData */}
        {/* ... */}
      </div>
      <div>
        {/* Geri ve İleri düğmeleri */}
        <button onClick={handlePrevPage} disabled={currentPage === 0}>
          Geri
        </button>
        <button onClick={handleNextPage} disabled={currentPage === inputPages.length - 1}>
          İleri
        </button>
      </div>
    </div>
  );
}

export default LeadGenerationPage;
