import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // useHistory eklenmeli
import SignInSide from '../component/SignInSide';

function Login() {
  const history = useHistory(); // useHistory tanımlanıyor
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);

    const enteredEmail = data.get('email');
    const enteredPassword = data.get('password');

    // Burada gerçek bir doğrulama yapılmalı
    if (enteredEmail === 'doğruEmail' && enteredPassword === 'doğruŞifre') {
      // Doğru bilgiler girildiyse /admin sayfasına yönlendir
      history.push('/admin');
    } else {
      // Yanlış bilgiler girildiyse burada gerekli işlemleri yapabilirsiniz
      console.log('Hatalı giriş');
    }
  };

  return (
    <div>
      <SignInSide handleSubmit={handleSubmit} /> {/* handleSubmit prop olarak iletiliyor */}
    </div>
  );
}

export default Login;
