import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button'; // Bu sat覺r覺 ekledim
function LeadGenerationPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { inputPageCount, outputPageCount } = location.state || {};
  const [currentPage, setCurrentPage] = useState(1);
  const [inputPages, setInputPages] = useState([]);
  const [outputZZZ, setOutputZZZ] = useState(0);
  const [outputYYY, setOutputYYY] = useState(0);

  useEffect(() => {
    if (inputPageCount && outputPageCount) {
      const pages = [];

      for (let i = 0; i < inputPageCount; i++) {
        pages.push({
          title: `Input Page ${i + 1}`,
          description: `Description for Input Page ${i + 1}`,
          image: `path_to_image_${i + 1}`,
          textInputVar: `AAA_${i + 1}`,
          selectInputVar: `CCC_${i + 1}`,
        });
      }

      setInputPages(pages);
    }
  }, [inputPageCount, outputPageCount]);

  useEffect(() => {
    // Calculate ZZZ and YYY based on input values
    const calculateZZZ = () => {
      let ZZZ = 0;

      for (const page of inputPages) {
        ZZZ += 2 * (Number(page.textInputVar) + 10);
      }

      return ZZZ;
    };

    const calculateYYY = (ZZZ) => {
      let YYY = 0;

      for (const page of inputPages) {
        YYY += 3 * Number(page.selectInputVar);
      }

      YYY += ZZZ;
      return YYY;
    };

    const ZZZ = calculateZZZ();
    const YYY = calculateYYY(ZZZ);

    setOutputZZZ(ZZZ);
    setOutputYYY(YYY);
  }, [inputPages]);

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, inputPages.length));
  };

  const handleBack = () => {
    navigate('/app');
  };

  if (inputPages.length === 0) {
    return <div>Loading...</div>;
  }

  const currentPageData = inputPages[currentPage - 1];

  return (
    <div>
      <h1>Lead Generation Page</h1>
      <div>
        <img src={currentPageData.image} alt={`Image for ${currentPageData.title}`} />
        <h2>{currentPageData.title}</h2>
        <p>{currentPageData.description}</p>
        <p>Text Input: {currentPageData.textInputVar}</p>
        <p>Select Input: {currentPageData.selectInputVar}</p>
      </div>
      <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <button onClick={handleNextPage} disabled={currentPage === inputPages.length}>
          Next
        </button>
      </div>
      <div>
        <h2>Output</h2>
        <p>ZZZ: {outputZZZ}</p>
        <p>YYY: {outputYYY}</p>
      </div>
      <Button variant="contained" color="secondary" onClick={handleBack}>
        Back to Admin Configuration
      </Button>
    </div>
  );
}

export default LeadGenerationPage;
